Console.WriteLine("Digite seu nome: "); string nome = Console.ReadLine(); Console.WriteLine($"Olá, seja bem-vinde {nome}");for ( int i = 0; i <= 0; i++){Console.WriteLine("Por favor, digite a operação que você quer fazer: ");Console.WriteLine("1 - Soma");Console.WriteLine("2 - Subtração");Console.WriteLine("3 - Multiplicação");Console.WriteLine("4 - Divisão");Console.WriteLine("0 - Sair");i = Convert.ToInt16(Console.ReadLine());Console.WriteLine("Digite o primeiro número: ");int x = Convert.ToInt16(Console.ReadLine());Console.WriteLine("Digite o segundo número: ");int y = Convert.ToInt16(Console.ReadLine());switch (i) {case 1:Console.WriteLine(x + y); break; case 2:Console.WriteLine(x  - y); break;case 3:Console.WriteLine(x * y); break;case 4:Console.WriteLine(x / y); break;}}